# collatz sequencedef collatz(number):    if number % 2 == 0:    # the number is even        return number // 2    elif number % 2 == 1:    # the number is odd        return 3 * number + 1while True:    try:        print("Enter an Integer:")        inputnumber = int(input())        result = collatz(inputnumber)    except ValueError:        print("Please enter an integer only")        continue    else:        while result:            print(result)            if result == 1:                break            else:                result = collatz(result)    break